import { React, useState, useEffect } from "react";

const useDarkMode = () => {
    const [dark, setDark] = useState(false);

    useEffect(() => {
        dark ? setDark(false) : setDark(true);
    });
    // const darkToggler = () => {
    //     dark ? setDark(false) : setDark(true);
    // };
    return dark;
};

export default useDarkMode;
