import React from "react";
import "./transactionPage.css";
import instance from "./axios.js";
import { useEffect, useState } from "react";

const Transaction = (account) => {
    return (
        <tr>
            <td>{account.name}</td>
            <td>{account.subtype}</td>
            <td>{account.balance.current}</td>
        </tr>
    );
};

const TransactionPage = () => {
    const [page, setPage] = useState(0);
    const [mainAcc, setMainAcc] = useState({ accounts: [] });
    const [loading, setLoading] = useState(true);
    // var mainAcc = {};

    const renderAccounts = async () => {
        try {
            await instance
                .post("/balance", {
                    access_token: "access-sandbox-4acad0a3-4e55-4ecc-8a16-8114123c22c4",
                })
                .then((res) => {
                    console.log(res);

                    setMainAcc({ accounts: res.data.accounts });
                    // mainAcc = { accounts: res.data };
                    console.log("acc2");
                    console.log(mainAcc);
                    setLoading(false);
                });
        } catch (err) {
            console.log(err);
        }
    };

    useEffect(() => {
        renderAccounts();
        setPage(page + 1);
    }, []);

    const transactionList = (page) => {
        console.log("acc");
        console.log(mainAcc.accounts);
        // return <h1>hi</h1>
        return mainAcc.accounts?.map((currentAccount) => {
            return <Transaction account={currentAccount} />;
            // console.log("?");
            // return <h1>hello</h1>;
        });
    };

    return (
        <div id="background">
            <div id="container">
                {loading ? (
                    <p>Currently Loading...</p>
                ) : (
                    <table>
                        <thead>
                            <tr>
                                <th>Institution Name</th>
                                <th>Account Type</th>
                                <th>Balance</th>
                            </tr>
                        </thead>
                        <tbody>{transactionList()}</tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default TransactionPage;
