import { React, useState, useEffect } from "react";

const useDarkMode = () => {
    const [dark, setDark] = useState(false);
    const darkToggler = () => {
        dark ? setDark(false) : setDark(true);
    };
    useEffect(() => {});
    return [dark, setDark];
};

export default useDarkMode;
