import React, { useState, useEffect } from "react";
import { Dropdown, DropdownButton, ButtonGroup } from "react-bootstrap";
// We import bootstrap to make our application look better.
import "./navbar.css";
import "bootstrap/dist/css/bootstrap.css";

import logo from "./icons/default-user-icon-trim.jpg";

// We import NavLink to utilize the react router.
import { NavLink } from "react-router-dom";

// Here, we display our Navbar
const Navbar = () => {
    const [dark, setDark] = useState(true);

    const darkToggler = () => {
        dark ? setDark(false) : setDark(true);
    };

    return (
        <div>
            <nav id={`${dark ? "dark" : "light"}`} className="navbar">
                <NavLink className="nav-brand" to="/">
                    ELSITO
                </NavLink>
                <button
                    className="navbar-toggler"
                    type="button"
                    data-toggle="collapse"
                    data-target="#navbarSupportedContent"
                    aria-controls="navbarSupportedContent"
                    aria-expanded="false"
                    aria-label="Toggle navigation"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/create">
                                Create Record
                            </NavLink>
                        </li>
                    </ul>
                </div>
                <button onClick={() => darkToggler()}>Dark Mode</button>

                <Dropdown className="logo">
                    <Dropdown.Toggle id="dropdown-basic" className="logo">
                        <img src={logo} id="UserLogo" alt="text" />
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item eventKey="1">Account Settings</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            </nav>
        </div>
    );
};

export default Navbar;
