import { React, useState, useEffect } from "react";

const useDarkMode = () => {
    const [darkMode, setDarkMode] = useState(false);

    useEffect(() => {
        darkMode ? setDarkMode(false) : setDarkMode(true);
    }, []);
    // const darkToggler = () => {
    //     dark ? setDark(false) : setDark(true);
    // };
    return darkMode;
};

export default useDarkMode;
