import React, { useState, useEffect } from "react";
import { Button, Dropdown } from "react-bootstrap";
// We import bootstrap to make our application look better.
import "./navbar.css";
import "bootstrap/dist/css/bootstrap.css";

import { FiSun, FiMoon } from "react-icons/fi";

import logo from "./icons/default-user-icon.jpg";

// We import NavLink to utilize the react router.
import { NavLink } from "react-router-dom";
import useDarkMode from "./darkMode.js";

// Here, we display our Navbar
const Navbar = () => {
    const [dark, setDark] = useState("dark");
    const isDark = useDarkMode();

    const DarkToggler = () => {
        dark ? setDark(false) : setDark(true);
    };
    // const [show, handleShow] = useState(false);

    // useEffect(() => {
    //     window.addEventListener("scroll", () => {
    //         if (window.scrollY > 100) {
    //             handleShow(true);
    //         } else handleShow(false);
    //     });
    //     return () => {
    //         window.removeEventListener("scroll");
    //     };
    // }, []);

    // const darkToggler = () => {
    //     dark ? setDark(false) : setDark(true);
    // };

    return (
        <div>
            <nav id={`${isDark ? "dark" : "light"}`} className="navbar">
                <div className="nav-brand">
                    <NavLink id={`${isDark ? "dark" : "light"}`} className="nav-brand" to="/">
                        ELSITO
                    </NavLink>
                </div>
                <Button flex="1" className="hidden" onClick={() => DarkToggler()}>
                    {isDark ? <FiSun size={30} /> : <FiMoon size={30} color="black" />}
                </Button>

                <Dropdown className="hidden" flex="1">
                    <Dropdown.Toggle
                        variant="light"
                        className="bg-white border-0 p-0"
                        id="dropdown-basic"
                    >
                        <img src={logo} id="UserLogo" alt="text" />
                    </Dropdown.Toggle>
                    <Dropdown.Menu flip={false} align="end" className="usermenu">
                        <Dropdown.Item href="#/action-2">Account Settings</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Sign out</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            </nav>
        </div>
    );
};

export default Navbar;
